import Particles from "react-tsparticles"
import { loadFull } from "tsparticles"
import particlesConfig from "./particle-config";
import React from "react";



function Particle() {
    const particlesInit = async (main) =>{
        console.log(main)


        await loadFull(main);
    };

    const particlesLoaded = (container) =>{
        console.log(container)
    };


    return(
        <Particles
            options={{
                fullScreen: {
                  enable: true,
                  zIndex: -10
                },
                    particles: {
                      number: {
                        value: 80,
                        density: {
                          enable: true,
                          value_area: 800
                        }
                      },
                      color: {
                        value: "#ffffff"
                      },
                      shape: {
                        type: "circle",
                        stroke: {
                          width: 0,
                          color: "#000000"
                        },
                        polygon: {
                          nb_sides: 6
                        },
                        image: {
                          src: "img/github.svg",
                          width: 100,
                          height: 100
                        }
                      },
                      opacity: {
                        value: 0.42613972648142723,
                        random: false,
                        anim: {
                          enable: false,
                          speed: 1,
                          opacity_min: 0.1,
                          sync: false
                        }
                      },
                      size: {
                        value: 4,
                        random: true,
                        anim: {
                          enable: false,
                          speed: 30,
                          size_min: 0.1,
                          sync: false
                        }
                      },
                      line_linked: {
                        enable: true,
                        distance: 150,
                        color: "#ffffff",
                        opacity: 0.4,
                        width: 1
                      },
                      move: {
                        enable: true,
                        speed: 6,
                        direction: "none",
                        random: false,
                        straight: false,
                        out_mode: "out",
                        bounce: false,
                        attract: {
                          enable: false,
                          rotateX: 600,
                          rotateY: 1200
                        }
                      }
                    },
                    interactivity: {
                      detect_on: "canvas",
                      events: {
                        onhover: {
                          enable: true,
                          mode: "repulse"
                        },
                        onclick: {
                          enable: true,
                          mode: "push"
                        },
                        resize: true
                      },
                      modes: {
                        grab: {
                          distance: 400,
                          line_linked: {
                            opacity: 1
                          }
                        },
                        bubble: {
                          distance: 400,
                          size: 40,
                          duration: 2,
                          opacity: 8,
                          speed: 3
                        },
                        repulse: {
                          distance: 200,
                          duration: 0.4
                        },
                        push: {
                          particles_nb: 4
                        },
                        remove: {
                          particles_nb: 2
                        }
                      }
                    },
                    retina_detect: true
              }}/>
    )
}

export default Particle;
