import React,{useEffect} from 'react'
import ScreenHeading from '../../utilities/ScreenHeading/ScreenHeading'
import ScrollService from '../../utilities/ScrollService'
import Animations from '../../utilities/Animations'

export default function About(props) {
  let fadeInScreenHandler = (screen)=>{
    if(screen.fadeScreen !== props.id)
    return
    Animations.animations.fadeInScreen(props.id)
  }

  const fadeInSubscription = ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler)
  
    const SCREEN_CONST = {
      description: "Data analytics M.Sc. student at LUT University. Future professional with business digitalization and data-oriented management with comprehensive programming and data analytics knowledge. Recognized for ability to work under pressure, learn quickly and develop ideas outside the box."
    }

  return (
    <div className='about_me_container screen_container' id={props.id || ""}>
        <div className='about_me_parent'>
            <ScreenHeading title={'About me'} subHeading={'Who am i?'}/>
        </div>
    </div>
  )
}
