import React, { useState, useEffect } from "react";
import ScreenHeading from '../../utilities/ScreenHeading/ScreenHeading'
import ScrollService from '../../utilities/ScrollService'
import Animations from '../../utilities/Animations'


export default function Resume(props) {
    const [selectedBulletIndex, setSelectedBulletIndex] = useState(0)
    const [carousalOffSetStyle, setCarousalOffSetStyle] = useState({})

    const Resume = (props) =>{
        <div className="resume_heading">
            <div className="resume_main_heading">
                <div className="heading_bullet">
                    <span>{props.heading ? props.heading : ""}</span>
                    {props.fromDate && props.toDate ?(
                        <dov className="heading_date">
                            {props.fromDate +"_"+props.toDate}
                        </dov>
                    ) : (
                        <div></div>
                    )}
                </div>
                <div className="resume_sub_heading">
                    <span>{props.subHeading ? props.subHeading : ""}</span>
                </div>
                <div className="resume_heading_description">
                    <span>{props.description ? props.description: ""}</span>
                </div>
            </div>
        </div>
    };


    const resumeBullets =[
        {label: "Education", logoSrc: "education.svg"},
        {label: "Work History", logoSrc: "work-history-svg"}
    ]

    let fadeInScreenHandler = (screen) => {
        if (screen.fadeScreen !== props.id) return;
        Animations.animations.fadeInScreen(props.id);
      };
    
      const fadeInSubscription =
        ScrollService.currentScreenFadeIn.subscribe(fadeInScreenHandler);

    return (
    <div className='resume_container screen_container' id ={props.id || ""}>
        <div className='resume_content'>
            <ScreenHeading title={"Resume"} subHeading={"Bio Details"}/>
        </div>

    </div>
  )
}
